class AssistantLoader {
    constructor() {
        this.assistants = []
        this.assistantsLoaded = false
        this.assistantCallbacks = []
    }

    loadAssistants() {
        fetch("https://actions-logging.mybluemix.net/logging/assistants")
        .then(res => res.json())
        .then((result) => {
            this.assistants = result
            this.assistantsLoaded = true

            for(var i = 0; i < this.assistantCallbacks.length; i++)
                this.assistantCallbacks[i](this.assistants)
        }, (error) => {
            console.log("Failed to get assistants")
        })
    }

    getAssistants(callback) {
        if (!this.assistantsLoaded)
            this.assistantCallbacks.push(callback)
        else callback(this.assistants)
    }
}

let instance = AssistantLoader()
export default instance
