import React from 'react';
import { Link } from 'react-router-dom';import {
    Help20,
    UserAvatar20,
  } from '@carbon/icons-react';
import {
  Header,
  HeaderName,
  HeaderGlobalBar,
  HeaderGlobalAction,
  Dropdown,
  DropdownSkeleton
} from 'carbon-components-react';

import './actions-header.scss';
import { connect } from 'react-redux';


let instance

class ActionsHeader extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            assistantsLoaded: false,
            selectedAssistant: "",
            assistants: []
        }

        this.props.assistantManager.getAssistants((assistants) => {
            this.setState({
                assistants: assistants,
                assistantsLoaded: true,
                selectedAssistant: assistants[0]
            });
        })

        instance = this


    }

    dropdownChanged(change) {
        this.props.dispatch({ type: "ASSISTANT_SELECTED", assistant: change.selectedItem })
    }

    render () {
        return <Header aria-label="Carbon Tutorial">
                    <HeaderName element={Link} to="/" prefix="IBM">
                    Actions Dashboard
                    </HeaderName>
                    <div id="headerDivider"></div>
                    <div style={{ width: 185 }}>
                    { this.props.assistantsLoaded
                    ? <Dropdown
                        type="inline"
                        label="Assistant"
                        ariaLabel="Dropdown"
                        id="assistant-dropdown"
                        items={this.props.assistants}
                        selectedItem={this.props.selectedAssistant}
                        onChange={ this.dropdownChanged }/>
                    : <DropdownSkeleton/> }
                    </div>
                    <HeaderGlobalBar>
                        <HeaderGlobalAction aria-label="Help">
                            <Help20 />
                        </HeaderGlobalAction>
                        <HeaderGlobalAction aria-label="User Avatar">
                            <UserAvatar20 />
                        </HeaderGlobalAction>
                    </HeaderGlobalBar>
                </Header>
    }
}

export default connect((state) => {
    return {
        assistantsLoaded: state.assistantsLoaded,
        assistants: state.assistants,
        selectedAssistant: state.selectedAssistant
    }
})(ActionsHeader)
