import React from 'react';
import './percent-ring.scss';

export default class PercentRing extends React.Component {
    constructor(props) {
        super(props)

        this.width = 250
        this.stroke = 16
        this.circumference = (this.width / 2 - 2 * this.stroke) * 2 * Math.PI

        this.state = {
            percent: parseInt(props.percent),
        }
    }

    render () {
        return <div className="rings-wrapper" style={{ width: this.width, height:this.width}}>
          <svg
            class="progress-ring"
            width={ this.width }
            height={ this.width }>
            <circle
              class="progress-ring__circle"
              stroke="#e98588"
              strokeWidth={ this.stroke }
              fill="transparent"
              r={ this.width / 2 - 2 * this.stroke }
              cx={ this.width / 2 }
              cy={ this.width / 2 } />
          </svg>
          <svg
            style={{ 
              strokeDasharray: `${this.circumference} ${this.circumference}`,
              strokeDashoffset: this.circumference - (this.state.percent / 100) * this.circumference,
              transform: "rotate(-90deg)" }}
            class="progress-ring"
            width={ this.width }
            height={ this.width }>
            <circle
              class="progress-ring__circle"
              stroke="#333333"
              strokeWidth={ this.stroke }
              fill="transparent"
              r={ this.width / 2 - 2 * this.stroke }
              cx={ this.width / 2 }
              cy={ this.width / 2 } />
          </svg>
          <div className="percent-div">
            <p className="percent-num">{this.state.percent}</p><p className="percent-sign">%</p>
          </div>
     </div>

    }
}
