import React from 'react';
import ReactDOM from 'react-dom';
import './index.scss';
import App from './App';
import * as serviceWorker from './serviceWorker';
import { HashRouter as Router } from 'react-router-dom';
import 'core-js/modules/es7.array.includes';
import 'core-js/modules/es6.array.fill';
import 'core-js/modules/es6.string.includes';
import 'core-js/modules/es6.string.trim';
import 'core-js/modules/es7.object.values';
import AssistantManager from './AssistantManager';
import { createStore } from 'redux'
import { Provider } from 'react-redux'

const initialState = {
    assistants: [],
    assistantsLoaded: false,
    selectedAssistant: "",
    logs: {}
}

let assistantManager = AssistantManager()
assistantManager.loadAssistants()

function reducer(state=initialState, action) {
    switch(action.type) {
        case "ASSISTANTS_LOADED":
            state.assistants = action.assistants
            state.selectedAssistant = action.selectedAssistant
            state.assistantsLoaded = true
            return state
        case "LOGS_LOADED":
            state.logs[action.assistantName] = action.logs
            return state
        case "ASSISTANT_SELECTED":
            state.selectedAssistant = action.assistant
            return state
        default:
            return state
    }
}

let store = createStore(reducer)

ReactDOM.render(<Provider store={store}><Router><App/></Router></Provider>, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
