import React from 'react';
import { connect } from 'react-redux';
import { LineChart } from "@carbon/charts-react";

import './graph.scss';
import moment from 'moment';

class Graph extends React.Component {
    constructor(props) {
        super(props)

    }

    render () {
      return <div className="graph-panel">
                <p className="panel-title">{ this.props.type + " over time" } </p>
                <LineChart
                    data={this.state.data}
                    options={this.state.options}>
		        </LineChart>
            </div>

    }
}

const mapStateToProps = (state, ownState) => {
  let props = {
      type: ownState.type,
      data: [],
      options: {
        data: {
            loading: true,
            height: "400px"
        }
      }
  }

  if (state.statsLoaded) {
      let leftAxis = props.type == "Engagment" ?
            {
                title: "volume",
                mapsTo: "value",
                scaleType: "linear"
            } :
            {
                title: props.type,
                mapsTo: "value",
                scaleType: "linear"
            }

      options = {
        axes: {
            "bottom": {
                "title": "Date",
                "mapsTo": "date",
                "scaleType": "time"
            },
            "left": {
                "mapsTo": "value",
                "title": "Conversion rate",
                "scaleType": "linear"
            }
        },
        height: "400px"
    }

  }

  return props
}

export default connect(mapStateToProps)(Graph)
