class AssistantManager {
    constructor(store) {
        this.store = store
    }

    loadAssistants() {
        fetch("https://actions-logging.mybluemix.net/logging/assistants")
        .then(res => res.json())
        .then((result) => {
            this.store.dispatch({
                type: "ASSISTANTS_LOADED",
                assistants: result
            })

        }, (error) => {
            console.log("Failed to get assistants")
        })
    }

    loadLogsForAssistant(assistantName) {
        fetch("https://actions-logging.mybluemix.net/logging/assistants?assistant=" + assistantName)
        .then(res => res.json())
        .then((result) => {
            this.store.dispatch({
                type: "LOGS_LOADED",
                logs: result,
                assistantName: assistantName 
            })

        }, (error) => {
            console.log("Failed to get logs")
        })
    }

}

export default AssistantManager
