import React from 'react';
import { connect } from 'react-redux';
import { LineChart } from "@carbon/charts-react";

import './graph.scss';
import moment from 'moment';

class Graph extends React.Component {
    constructor(props) {
        super(props)

    }

    render () {
      return <div className="graph-panel">
                <p className="panel-title" style={{ marginBottom: "16px" }}>{ this.props.type + " over time" } </p>
                <LineChart style={{ height: "400px" }}
                    data={this.props.data}
                    options={this.props.options}>
		        </LineChart>
            </div>

    }
}

const mapStateToProps = (state, ownState) => {
  let props = {
      type: ownState.type,
      data: [],
      options: {
        axes: {
            bottom: {
              "title": "Loading",
              "mapsTo": "date",
              "scaleType": "time"
            },
            left: {
              mapsTo: "value",
              title: "Loading",
              scaleType: "linear"
            }
          },
        data: {
            loading: true,
        },
        height: "400px"
      }
  }

  if (state.statsLoaded) {
      let leftAxis = props.type == "Engagment" ?
            {
                title: "volume",
                mapsTo: "value",
                scaleType: "linear"
            } :
            {
                title: props.type,
                mapsTo: "value",
                scaleType: "linear"
            }

      props.options = {
        axes: {
            bottom: {
                title: "Date",
                mapsTo: "date",
                scaleType: "time"
            },
            left: leftAxis
        },
        height: "400px"
    }

  }

  return props
}

export default connect(mapStateToProps)(Graph)
