import React from 'react';
import './percent-ring.scss';

export default class PercentRing extends React.Component {
    constructor(props) {
        super(props)

        this.width = 250
        this.stroke = 16
        this.circumference = (this.width / 2 - 2 * this.stroke) * 2 * Math.PI

        this.state = {
            percent: props.percent,
        }
    }

    render () {
        return <div className="rings-wrapper">
          <svg
            class="progress-ring"
            width={ this.width }
            height={ this.width }>
            <circle
              class="progress-ring__circle"
              stroke="#ee5396"
              stroke-width={ this.stroke }
              fill="transparent"
              r={ this.width / 2 - 2 * this.stroke }
              cx={ this.width / 2 }
              cy={ this.width / 2 } />
          </svg>
          <svg
            style={{ strokeDasharray: `${this.circumference} ${this.circumference}`, strokeDashoffset: this.state.percent * this.circumference }}
            class="progress-ring"
            width={ this.width }
            height={ this.width }>
            <circle
              class="progress-ring__circle"
              stroke="#333333"
              stroke-width={ this.stroke }
              fill="transparent"
              r={ this.width / 2 - 2 * this.stroke }
              cx={ this.width / 2 }
              cy={ this.width / 2 } />
          </svg>
     </div>

    }
}
