import React from 'react';
import { connect } from 'react-redux';
import { LineChart } from "@carbon/charts-react";
import { Checkbox, fieldset } from "carbon-components-react"
import './graph.scss';
import "@carbon/charts/styles.css";
import moment from 'moment';
import { from } from 'apollo-link';
import config from '../../config';

class Graph extends React.Component {
    constructor(props) {
        super(props)
    }

    render () {
      return <div className="graph-panel">
                <p className="panel-title" style={{ marginBottom: "16px" }}>{ this.props.type + " over time" } </p>
                { this.props.type == "Engagement" && <fieldset className="bx--fieldset" style={{ alignment: "horizontal" }}>
                <Checkbox labelText="Show active users" checked={ this.propsshowActiveUsers } id="activeUsersCB"
                     onChange={ (checked) => { this.props.dispatch({type: config.UPDATE, update: {engagement_showActiveUsers: !checked}}) } } />
                <Checkbox labelText="Show conversations" checked={ this.props.showConversations } id="conversationsCB" />
                <Checkbox labelText="Show requests" checked={ this.props.showRequests } id="requestsCB" />
                </fieldset> }
                <div className="chartWrapper">
                    <LineChart
                        data={this.props.data}
                        options={this.props.options}>
                    </LineChart>
                </div>
            </div>

    }
}

const mapStateToProps = (state, ownState) => {
  let props = {
      type: ownState.type,
      showActiveUsers: state.engagement_showActiveUsers,
      showConversations: state.engagement_showConversations,
      showRequests: state.engagement_showRequests,
      data: [],
      options: {
        axes: {
            bottom: {
              title: "Date",
              mapsTo: "date",
              scaleType: "time"
            },
            left: {
              mapsTo: "value",
              title: "Loading",
              scaleType: "linear"
            }
          },
        data: {
            loading: true,
        },
        legend: { enabled: false },
        height: "400px",
        color: { scale: { "Requests": "#9C74F7", "Active Users": "#38807E", "Conversations": "#DA5E93" } }
      }
  }

  if (state.statsLoaded) {
      let leftAxis = props.type == "Engagment" ?
            {
                title: "volume",
                mapsTo: "value",
                scaleType: "linear"
            } :
            {
                title: props.type,
                mapsTo: "value",
                scaleType: "linear"
            }

      props.options = {
        axes: {
            left: leftAxis
        },
        data: {
            loading: false,
        },
    }

    switch(props.type) {
        case "Engagement":
            props.data = loadEngagementData(state)
    }

  }

  return props
}

const loadEngagementData = (state) => {
    let data = []

    if (state.engagement_showActiveUsers) {
        for(let i = 0; i < Object.keys(state.uniqueUsers).length; i++) {
            let date = Object.keys(state.uniqueUsers)[i]

            data.push({
                date: moment(date, "L").toISOString(),
                value: state.uniqueUsers[date].count,
                group: "Active Users"
            })
        }
    }

    if (state.engagement_showConversations) {
        for(let i = 0; i < Object.keys(state.uniqueSessions).length; i++) {
            let date = Object.keys(state.uniqueSessions)[i]

            data.push({
                date: moment(date, "L").toISOString(),
                value: state.uniqueSessions[date],
                group: "Conversations"
            })
        }
    }

    if (state.engagement_showRequests) {
        for(let i = 0; i < Object.keys(state.requestCounts).length; i++) {
            let date = Object.keys(state.requestCounts)[i]

            data.push({
                date: moment(date, "L").toISOString(),
                value: state.requestCounts[date],
                group: "Requests"
            })
        }
    }

    console.log("LOADED ENGAGEMENT DATA: " + JSON.stringify(data))
    return data
}

export default connect(mapStateToProps)(Graph)
