import React from 'react';
import { Link } from 'react-router-dom';
import {
    Help20,
    UserAvatar20,
  } from '@carbon/icons-react';
import {
  Header,
  HeaderName,
  HeaderGlobalBar,
  HeaderGlobalAction,
  Dropdown,
  DropdownSkeleton
} from 'carbon-components-react';
import './actions-header.scss';

let instance

export default class ActionsHeader extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            assistantsLoaded: false,
            selectedAssistant: "",
            assistants: []
        }

        this.props.assistantLoader.getAssistants((assistants) => {
            this.setState({
                assistants: assistants,
                assistantsLoaded: true,
                selectedAssistant: assistants[0]
            });
        })

        instance = this
    }

    dropdownChanged(change) {
        instance.setState({
            selectedAssistant: change.selectedItem
        })
    }

    render () {
        return <Header aria-label="Carbon Tutorial">
                    <HeaderName element={Link} to="/" prefix="IBM">
                    Actions Dashboard
                    </HeaderName>
                    <div style={{ width: 185 }}>
                    { this.state.assistantsLoaded
                    ? <Dropdown
                        light={false}
                        label="Assistant"
                        ariaLabel="Dropdown"
                        id="assistant-dropdown"
                        items={this.state.assistants}
                        selectedItem={this.state.selectedAssistant}
                        onChange={ this.dropdownChanged }/>
                    : <DropdownSkeleton/> }
                    </div>
                    <HeaderGlobalBar>
                        <HeaderGlobalAction aria-label="Help">
                            <Help20 />
                        </HeaderGlobalAction>
                        <HeaderGlobalAction aria-label="User Avatar">
                            <UserAvatar20 />
                        </HeaderGlobalAction>
                    </HeaderGlobalBar>
                </Header>
    }
}
