import React from 'react';
import { Link } from 'react-router-dom';
import {
    Help20,
    UserAvatar20,
  } from '@carbon/icons-react';
import {
  Header,
  HeaderName,
  HeaderGlobalBar,
  HeaderGlobalAction,
  Dropdown,
  DropdownSkeleton
} from 'carbon-components-react';

export default class ActionsHeader extends React.Component {
    constructor() {
        super()
        this.state = {
            assistantsLoaded: false,
            selectedAssistant: "",
            assistants: []
        }
    }

    dropdownChanged(change) {
        this.setState({
            selectedAssistant: change.selectedItem
        })
    }

    componentDidMount() {
        that = this

        fetch("https://actions-logging.mybluemix.net/logging/assistants")
            .then(res => res.json())
            .then((result) => {
                that.setState({
                    assistants: result,
                    assistantsLoaded: true,
                    selectedAssistant: result[0]
                });
            }, (error) => {
                console.log("Failed to get assistants")
            })
    }

    render () {
        return <Header aria-label="Carbon Tutorial">
                    <HeaderName element={Link} to="/" prefix="IBM">
                    Actions Dashboard
                    </HeaderName>
                    <div style={{ width: 150 }}>
                    { this.state.assistantsLoaded
                    ? <Dropdown
                        label="Assistant"
                        ariaLabel="Dropdown"
                        id="assistant-dropdown"
                        items={this.state.assistants}
                        selectedItem={this.state.selectedAssistant}
                        onChange={ this.dropdownChanged }/>
                    : <DropdownSkeleton/> }
                    </div>
                    <HeaderGlobalBar>
                        <HeaderGlobalAction aria-label="Help">
                            <Help20 />
                        </HeaderGlobalAction>
                        <HeaderGlobalAction aria-label="User Avatar">
                            <UserAvatar20 />
                        </HeaderGlobalAction>
                    </HeaderGlobalBar>
                </Header>
    }
}
