import config from "./config"

class AssistantManager {
    constructor(store) {
        this.store = store
    }

    loadAssistants() {
        fetch(config.baseURL + "/assistants")
        .then(res => res.json())
        .then((result) => {
            this.store.dispatch({
                type: config.ASSISTANT_LOADED,
                assistants: result
            })

        }, (error) => {
            console.log("Failed to get assistants")
        })
    }

    loadLogsForAssistant(assistantName) {
        fetch(config.baseURL + "/assistants?assistant=" + assistantName)
        .then(res => res.json())
        .then((result) => {
            this.store.dispatch({
                type: config.STATS_LOADED,
                assistantName: assistantName,
                logs: result
            })

        }, (error) => {
            console.log("Failed to get logs")
        })
    }

}

export default AssistantManager
