import React from 'react';
import { Link } from 'react-router-dom';
import {
    SideNav,
    SideNavItems,
    SideNavLink
  } from 'carbon-components-react';
import './sidebar.scss';


export default class Sidebar extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            activePage: "Overview"
        }
    }

    linkClicked(page) {
        this.setState({
            activePage: page
        })
    }

    render () {
        return <SideNav
                isFixedNav
                expanded={true}
                isChildOfHeader={false}
                aria-label="Side navigation">
                <SideNavItems>
                    <SideNavLink element={Link} to="/"
                      onClick={() => this.linkClicked("Overview")}
                      isActive={this.state.activePage === "Overview"}>
                        Overview
                    </SideNavLink>
                    <SideNavLink element={Link} to="/engagement"
                      onClick={() => this.linkClicked("Engagement")}
                      isActive={this.state.activePage === "Engagement"}>
                        Engagement
                    </SideNavLink>
                </SideNavItems>
            </SideNav>
    }
}
