import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import {
    Help20,
    UserAvatar20,
  } from '@carbon/icons-react';
import {
  Header,
  HeaderContainer,
  HeaderName,
  HeaderGlobalBar,
  HeaderGlobalAction,
  Dropdown,
} from 'carbon-components-react';

export class ActionsHeader extends React.Component {

    constructor() {
        super()
        this.state = {
            assistants: [ "Assistant1", "Assistant2" ],
            selectedAssistant: "Assistant1"
        }
    }

    dropdownChanged = () => {
        this.state.selectedItem = "Assistant2"
    }

    render () {
        <HeaderContainer>
            <Header aria-label="Carbon Tutorial">
                <HeaderName element={Link} to="/" prefix="IBM">
                Actions Dashboard
                </HeaderName>
                <Dropdown
                    label="Assistant"
                    style={{ width: 150 }}
                    ariaLabel="Dropdown"
                    id="assistant-dropdown"
                    items={this.state.assistants}
                    selectedItem={this.state.selectedAssistant}
                    onChange={dropdownChanged()}/>
                <HeaderGlobalBar>
                    <HeaderGlobalAction aria-label="Help">
                        <Help20 />
                    </HeaderGlobalAction>
                    <HeaderGlobalAction aria-label="User Avatar">
                        <UserAvatar20 />
                    </HeaderGlobalAction>
                </HeaderGlobalBar>
            </Header>
        </HeaderContainer>
    };

}
