import React from 'react';
import { Link } from 'react-router-dom';
import {
    AppSwitcher20,
    Help20,
    UserAvatar20,
  } from '@carbon/icons-react';
import {
  Header,
  HeaderContainer,
  HeaderName,
  HeaderNavigation,
  HeaderMenuButton,
  HeaderMenuItem,
  HeaderGlobalBar,
  HeaderGlobalAction,
  SkipToContent,
  SideNav,
  SideNavItems,
  HeaderSideNavItems,
  Dropdown,
} from 'carbon-components-react';

let dropdownItems = ["Assistant1", "Assistant2"]

function dropdownChanged (event) {
    console.log("DROPDOWN CHANGED")
    // this.setState({ value: event.target.value });
};

const ActionsHeader = () => (
  <HeaderContainer
    render={({ isSideNavExpanded, onClickSideNavExpand }) => (
      <Header aria-label="Carbon Tutorial">
        <HeaderName element={Link} to="/" prefix="IBM">
        Actions Dashboard
        </HeaderName>
        <Dropdown
            style={{ width: 150 }}
            ariaLabel="Dropdown"
            id="assistant-dropdown"
            items={dropdownItems}
            selectedItem={dropdownItems[0]}
            onChange={dropdownChanged()}/>
        <HeaderGlobalBar>
            <HeaderGlobalAction aria-label="Help">
                <Help20 />
            </HeaderGlobalAction>
            <HeaderGlobalAction aria-label="User Avatar">
                <UserAvatar20 />
            </HeaderGlobalAction>
        </HeaderGlobalBar>
      </Header>
    )}
  />
);

export default ActionsHeader;
