import React from 'react';
import { Link } from 'react-router-dom';
import {
  ArrowRight16
} from '@carbon/icons-react';

import './overview-table.scss';

export default class OverviewTable extends React.Component {
    constructor(props) {
        super(props)

        this.titleDict = {
          "mostCommon": "Most common topics",
          "leastCommon": "Least common topics",
          "frequentlyEscalated": "Frequently escalated topics",
          "notRecognized": "Requests not recognized"
        }

        this.state = {
          type: props.type,
          data: props.data,
          maxTopicCount: props.maxTopicCount
        }
    }

    render () {
      let divider = <div style={{ width: "100%", height: "1px", background: "#f4f4f4", marginBottom: "2px" }}></div>
      var tableRows = []

      var data = [...this.state.data]

      console.log("A: " + JSON.stringify(data))

      for (var i = 0; i < Math.max(10 - data.length, 0); i++) 
        data.push(null)

      console.log("B: " + JSON.stringify(data))

      for (var i = 0; i < Math.min(10, data.length); i++) {
        let val = data[i]

        if (this.state.type === "notRecognized") {
          val = val ?? { label: "-" }

          tableRows.push(<div className="overview-table-row">
                          <p className="overview-table-label">{val.label}</p>
                          { divider }
                        </div>)
        } else {
          if (val) {
            tableRows.push(<div className="overview-table-row">
                            <p className="overview-table-num-label">{ val.label }</p>
                            <p className="overview-table-num">{ this.state.type === "frequentlyEscalated" ? val.count  + "%" : val.count}</p>
                            <div className="overview-table-progress-container">
                              <div className="overview-table-progress-back"/>
                              <div className={ this.state.type === "mostCommon" ? "overview-table-green-bar" : "overview-table-red-bar" } 
                                style={{ width: (this.state.type === "frequentlyEscalated" ? val.count : (100 * (val.count / this.state.maxTopicCount))) + "%" }}/>
                            </div>
                          </div>)
          } else {
            tableRows.push(<div className="overview-table-row">
                            <div className="overview-table-progress-container overview-table-no-value">
                              <div className="overview-table-progress-back"/>
                            </div>
                          </div>)
          }
        }
      }

      return <div className="overview-table">
              <p className="panel-title">{ this.titleDict[this.state.type] }</p>
              <p className="overview-table-date">Aug 08 2021 - Sep 07 2021</p>
              { divider }
              <ul>
                { tableRows.map((value, index) => {
                  return <li key={index}>{value}</li>
                }) }
              </ul>
              {(this.state.type === "notRecognized") && 
                 <Link className='panel-link-div' to="/not-recognized">
                  <p className="panel-link">View all</p><ArrowRight16/>
                 </Link> }
            </div>

    }
}
