import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import {
    Help20,
    UserAvatar20,
  } from '@carbon/icons-react';
import {
  Header,
  HeaderContainer,
  HeaderName,
  HeaderGlobalBar,
  HeaderGlobalAction,
  Dropdown,
} from 'carbon-components-react';

export default class ActionsHeader extends React.Component {

    constructor() {
        super()
        this.state = {
            assistants: [ "Assistant1", "Assistant2" ],
            selectedAssistant: "Assistant1"
        }
    }

    dropdownChanged() {
        var dropdown = document.getElementById("assistant-dropdown")
        console.log(dropdown)

        if (dropdown)
            console.log(dropdown.selectedItem)
            // if (this.state.selectedAssistant != dropdown.selectedItem)
            //     this.setState({
            //         selectedAssistant: "Assistant2"
            //     });
    }

    render () {
        return <Header aria-label="Carbon Tutorial">
                    <HeaderName element={Link} to="/" prefix="IBM">
                    Actions Dashboard
                    </HeaderName>
                    <Dropdown
                        label="Assistant"
                        style={{ width: 150 }}
                        ariaLabel="Dropdown"
                        id="assistant-dropdown"
                        items={this.state.assistants}
                        selectedItem={this.state.selectedAssistant}
                        onChange={this.dropdownChanged()}/>
                    <HeaderGlobalBar>
                        <HeaderGlobalAction aria-label="Help">
                            <Help20 />
                        </HeaderGlobalAction>
                        <HeaderGlobalAction aria-label="User Avatar">
                            <UserAvatar20 />
                        </HeaderGlobalAction>
                    </HeaderGlobalBar>
                </Header>
    }
}
